package 증감연산자;

public class OperatorEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c = 'a';
		for(int i = 0; i<26; i++) {
			System.out.print(c++);
		}
		System.out.println();
		
		c = 'A';
		for(int i = 0; i<26; i++) {
			System.out.print(c++);
		}
		System.out.println();
	
	c='0';
	for(int i = 0; i < 10; i++) {
		System.out.print(c++);
	}
	System.out.println();
	}
}
